"use strict";

var navContainer = document.getElementById("main-nav-list"),
  navLinksCollection = navContainer.getElementsByClassName("main-navigation__item"),
  navLinkActiveClass = "main-navigation__item--active",
  activePagePath = window.location.pathname,
  isMainPage = "/" === activePagePath;

for (var a = 0; a < navLinksCollection.length; a++) {
  var n = navLinksCollection[a];
  n.classList.remove(navLinkActiveClass),
    n.href.includes("" + activePagePath) &&
      !isMainPage &&
      n.classList.add(navLinkActiveClass);
}

var curtainMenu = document.getElementById("curtain-menu");

function toggleCurtainMenu() {
  if (curtainMenu.classList.contains("curtain-menu--open")) {
    curtainMenu.classList.remove("curtain-menu--open");
  } else {
    curtainMenu.classList.add("curtain-menu--open");
  }
}

function myFunction(x) {
  x.classList.toggle("change");
}
